import React from 'react';
import '../css/navbar.css';
import Logo from '../img/logo.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faHome, faBook, faKey, faUsers, faCalendar, faFlag } from '@fortawesome/fontawesome-free-solid';
import Courses from './Courses';
function Navbar() {
  return (
    <div className='container-fluid'>
        <div className='nav-top'>
            <img src={Logo} alt="logo" />
            <div className='search-input'>
              <FontAwesomeIcon icon={faSearch} size={30} style={{
                  marginLeft: 10,
                  marginRight: 10,
                  marginTop: 11,
                  color: '#626262',
              }}/>
              <input type="search" placeholder="Search"/>
            </div>
            <div className='nav-button'>
              <a href='#'>Sign in</a>
              <a href='#' className=''>Sign up</a>
            </div>
        </div>
        <div className='nav-side' id='navbar-side'>
          <ul>
            <li className='list-btn active'>
              <FontAwesomeIcon icon={faHome} size={50} style={{
                  marginRight: 10,
              }}/>
              Home
            </li>
            <li className='list-btn'>
              <FontAwesomeIcon icon={faBook} size={50}  style={{
                  marginRight: 10,
              }}/>
              Courses
            </li>
            <li className='list-btn'>
              <FontAwesomeIcon icon={faKey} size={50}  style={{
                  marginRight: 10,
              }}/>
              Transactions
            </li>
            <li className='list-btn'>
              <FontAwesomeIcon icon={faUsers} size={50}  style={{
                  marginRight: 10,
              }}/>
              Members
            </li>
            <li className='list-btn'>
              <FontAwesomeIcon icon={faCalendar} size={50}  style={{
                  marginRight: 10,
              }}/>
              Membership Catalog
            </li>
            <li className='list-btn'>
              <FontAwesomeIcon icon={faFlag} size={50}  style={{
                  marginRight: 10,
              }}/>
              Terms of Service
            </li>
          </ul>
        </div>
        <div className='content'>
            <Courses />
        </div>
    </div>
  )
}

function  navside_list(){
  // Get the container element
  var btnContainer = document.getElementById("navbar-side");

  // Get all buttons with class="btn" inside the container
  var btns = btnContainer.getElementsByClassName("list-btn");

  // Loop through the buttons and add the active class to the current/clicked button
  for (var i = 0; i < btns.length; i++) {
      btns[i].addEventListener("click", function() {
          var current = document.getElementsByClassName("active");
          current[0].className = current[0].className.replace(" active", "");
          this.className += " active";
      });
  }
}

export default Navbar
